const BAR_CHART_OPTIONS={responsive:!0,scales:{yAxes:[{ticks:{beginAtZero:!0}}]},legend:{position:"top"}};function dataBarChartHandler(a){let t=[];return $.each(a,(a,e)=>{t.push({label:e.name.escape(),backgroundColor:`#${randomHexColor()}`,data:[e.total]})}),t}async function brandChartHandler(){let a={labels:["Total"],datasets:dataBarChartHandler([{name:"Brand 1",total:10},{name:"Brand 2",total:15},{name:"Brand 3",total:8},{name:"Brand 4",total:0},{name:"Brand 5",total:13}])},t=$("#brand-chart").get(0).getContext("2d");new Chart(t,{type:"bar",data:a,options:BAR_CHART_OPTIONS})}async function categoryChartHandler(){let a={labels:["Total"],datasets:dataBarChartHandler([{name:"Category 1",total:33},{name:"Category 2",total:45},{name:"Category 3",total:4},{name:"Category 4",total:10},{name:"Category 5",total:13}])},t=$("#category-chart").get(0).getContext("2d");new Chart(t,{type:"bar",data:a,options:BAR_CHART_OPTIONS})}async function dashboardChartRun(){await brandChartHandler(),await categoryChartHandler()}$(document).ready(()=>{DASHBOARD_PAGE&&dashboardChartRun()});
