function getStyleHtml(){const t=document.getElementsByTagName("link");let n="";return $.each(t,(t,e)=>{n+=e.outerHTML}),n}function getPrintAreaHtml(){return $("#print-area")[0].outerHTML}function getPrintScript(){return $(window).width()>1e3?"\n            <script>\n                setTimeout(() => {\n                    window.print();\n                    window.close();\n                }, 200);\n            <\/script>\n        ":"\n        <script>\n            setTimeout(() => {\n                window.print();\n            }, 200);\n        <\/script>\n    "}function printArea(){const t=`\n        <html>\n            <head>\n                <meta charset="utf-8">\n                <meta http-equiv="X-UA-Compatible" content="IE=edge">\n                <title>${$("#print-btn").attr("data-title")}</title>\n                ${getStyleHtml()}\n            </head>\n            <body onload="window.print()">\n                <style>\n                    @media print {\n                        tr.bg-light td,\n                        tr.bg-light th {\n                            background-color: #f8f9fa !important;\n                            -webkit-print-color-adjust: exact;\n                        }\n                    }\n                </style>\n                ${getPrintAreaHtml()}\n                ${getPrintScript()}\n            </body>\n        </html>\n    `;window.open("","_blank").document.write(t)}$(document).ready(()=>{$("#print-btn").click(()=>{printArea()}),$("#pdf-btn").click(()=>{downloadPdf()})});
