function getReportStyle(){if($("#stock-report-content").length>0)return"\n            .dataTable thead tr th:nth-child(1),\n            .dataTable thead tr th:nth-child(2),\n            .dataTable thead tr th:nth-child(3),\n            .dataTable tbody tr td:nth-child(1),\n            .dataTable tbody tr td:nth-child(2),\n            .dataTable tbody tr td:nth-child(3)  {\n                text-align: center;\n            }\n\n            .dataTable thead tr th:nth-child(5),\n            .dataTable thead tr th:nth-child(6),\n            .dataTable tbody tr td:nth-child(5),\n            .dataTable tbody tr td:nth-child(6)  {\n                text-align: right; \n            }\n        "}function initPrintDatatable(){$(".print-table").DataTable({dom:"Bfrtip",buttons:[{autoPrint:$(window).width()>1e3,extend:"print",text:'<i class="fas fa-print mr-1"></i>PDF/Cetak',className:"btn btn-sm btn-danger",customize:function(t){$(t.document.body).prepend(`\n                            <style>\n                                ${getReportStyle()}\n                            </style>\n                            <h3 class="text-center">${$(".print-table").attr("data-title")}</h3>\n                        `),$(t.document.body).find("h1").eq(0).remove(),$(window).width()<1e3&&$(t.document.body).append("\n                            <script>\n                                let stateCheck = setInterval(() => {\n                                    if (document.readyState === 'complete') {\n                                        clearInterval(stateCheck);\n                                        window.print();\n                                    }\n                                }, 200);\n                            <\/script>\n                        ")}}]})}$(document).ready(()=>{$(".print-table").length>0&&initPrintDatatable()});
